// PDF.js Worker - Fallback local version
// This is a simplified local worker that falls back to no-worker mode if needed

if (typeof window !== 'undefined') {
  // Browser environment - try to load the actual worker
  try {
    importScripts('https://unpkg.com/pdfjs-dist@4.8.69/build/pdf.worker.min.js');
  } catch (e) {
    try {
      importScripts('https://cdn.jsdelivr.net/npm/pdfjs-dist@4.8.69/build/pdf.worker.min.js');
    } catch (e2) {
      // If all imports fail, this worker will fail gracefully and PDF.js will fallback to no-worker mode
      console.warn('PDF.js worker fallback failed, using no-worker mode');
    }
  }
} else {
  // Node.js environment - no worker needed
  console.log('PDF.js worker not needed in Node.js environment');
}